import React, { Component } from "react";
import "./Message.css";
import { Checkbox } from "antd";

export default class Message extends Component {
  constructor(props) {
    super(props);

    this.state = {
      color: "white"
    };
  }

  changeColor = () => {
    this.setState({ color: "#f2f2f2" });
  };

  changeWhite = () => {
    this.setState({ color: "white" });
  };

  changeWeight = () => {
    this.props.addClicked(this.props.id, this.props.emails);
  };

  parseDate =
    this.props.date.substring(8, 10) +
    "-" +
    this.props.date.substring(5, 7) +
    "-" +
    this.props.date.substring(0, 4);

  render() {
    return (
      <div
        className="message"
        onMouseEnter={this.changeColor}
        onMouseLeave={this.changeWhite}
        onClick={this.changeWeight}
        style={{
          backgroundColor: this.state.color,
          fontWeight: fontWeight
        }}
      >
        <div className="leftSection">
          <Checkbox className="checkBox" autoFocus="true" />
          {this.props.from}
        </div>

        <div className="midSection">{this.props.subject}</div>

        <div className="rightSection">{this.parseDate}</div>
      </div>
    );
  }
}
